mapper.select:
SELECT main.`SCHED_NAME`, main.`TRIGGER_NAME`, main.`TRIGGER_GROUP`, main.`DESCRIPTION`, main.`NEXT_FIRE_TIME`, main.`PREV_FIRE_TIME`, main.`TRIGGER_STATE`, sub.`CRON_EXPRESSION`, sub.`TIME_ZONE_ID`, job.`JOB_NAME`, job.`JOB_GROUP`, job.`IS_DURABLE`, job.`IS_NONCONCURRENT`, job.`IS_UPDATE_DATA`, job.`JOB_CLASS_NAME` FROM `qrtz_triggers` main INNER JOIN `qrtz_cron_triggers` sub ON main.`SCHED_NAME` = sub.`SCHED_NAME` AND main.`TRIGGER_NAME` = sub.`TRIGGER_NAME` AND main.`TRIGGER_GROUP` = sub.`TRIGGER_GROUP` INNER JOIN `qrtz_job_details` job ON main.`JOB_NAME` = job.`JOB_NAME` AND main.`JOB_GROUP` = job.`JOB_GROUP` WHERE true
AND main.`trigger_name`=#{trigger_name}
AND main.`trigger_group` LIKE CONCAT('%',#{trigger_group},'%')
AND main.`description` LIKE CONCAT('%',#{description},'%')
AND main.`trigger_state`=#{trigger_state}

SELECT main.`SCHED_NAME`, main.`TRIGGER_NAME`, main.`TRIGGER_GROUP`, main.`DESCRIPTION`, main.`NEXT_FIRE_TIME`, main.`PREV_FIRE_TIME`, main.`TRIGGER_STATE`, sub.`CRON_EXPRESSION`, sub.`TIME_ZONE_ID`, job.`JOB_NAME`, job.`JOB_GROUP`, job.`IS_DURABLE`, job.`IS_NONCONCURRENT`, job.`IS_UPDATE_DATA`, job.`JOB_CLASS_NAME` FROM `qrtz_triggers` main INNER JOIN `qrtz_cron_triggers` sub ON main.`SCHED_NAME` = sub.`SCHED_NAME` AND main.`TRIGGER_NAME` = sub.`TRIGGER_NAME` AND main.`TRIGGER_GROUP` = sub.`TRIGGER_GROUP` INNER JOIN `qrtz_job_details` job ON main.`JOB_NAME` = job.`JOB_NAME` AND main.`JOB_GROUP` = job.`JOB_GROUP` WHERE true
AND main.`trigger_name`=#{jobName}
AND main.`trigger_group`=#{jobGroup}
